package com.java;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		   for ( long i = 0,  n = 1; i<= 10; i++ , n *=10) {				//10의 반복과 제곱의 반복을 위한 문
			   
			   						
			   System.out.println("10^" +String.format("%2d", i)  + "=" + String.format("%12d",n) );  //출력문
			   
			   
			   
			   
			   
		   }
		   
		
		
		
	}

}
